class HotPotatoGame {
  constructor(canvasId, gameState, updateScore, hotPotatoes, teamStats, currentUser, onTaskClick, onTaskComplete) {
    this.canvas = document.getElementById(canvasId);
    if (!this.canvas) {
      console.error('Canvas element not found');
      return;
    }

    this.ctx = this.canvas.getContext('2d');
    this.gameState = gameState;
    this.updateScore = updateScore;
    this.hotPotatoes = hotPotatoes;
    this.teamStats = teamStats;
    this.currentUser = currentUser;
    this.onTaskClick = onTaskClick;
    this.onTaskComplete = onTaskComplete;

    this.isRunning = false;
    this.animationId = null;
    this.spirits = [];
    this.particles = [];
    this.locations = [];
    this.selectedTask = null;
    this.showTaskModal = false;

    // Image assets
    this.images = {};
    this.imagesLoaded = false;
    this.loadImages();

    this.initializeLocations();
    this.handleClick = this.handleClick.bind(this);
    this.handleKeyPress = this.handleKeyPress.bind(this);
    this.gameLoop = this.gameLoop.bind(this);
    this.resizeCanvas();
  }

  async loadImages() {
    const imageUrls = {
      background: 'myimages/background.png',
      spirit1: 'myimages/spirit1.png', // common
      spirit2: 'myimages/spirit2.png', // rare
      spirit3: 'myimages/spirit3.png'  // epic
    };

    const loadPromises = Object.entries(imageUrls).map(([key, url]) => {
      return new Promise((resolve, reject) => {
        const img = new Image();
        img.onload = () => {
          this.images[key] = img;
          resolve();
        };
        img.onerror = () => {
          console.warn(`Failed to load image: ${url}`);
          // Create fallback for missing images
          this.images[key] = null;
          resolve();
        };
        img.src = url;
      });
    });

    try {
      await Promise.all(loadPromises);
      this.imagesLoaded = true;
      console.log('Images loaded successfully');
    } catch (error) {
      console.error('Error loading images:', error);
      this.imagesLoaded = true; // Continue without images
    }
  }

  initializeLocations() {
    // Positioning based on the background image layout
    this.locations = [
      { name: "Sales Office", x: 150, y: 320, radius: 40, color: "#FFCC00", tasks: [], number: 1 }, // Bottom left house
      { name: "New Lead", x: 250, y: 250, radius: 40, color: "#66B2FF", tasks: [], number: 2 }, // Empty lot
      { name: "New Customer", x: 350, y: 200, radius: 40, color: "#9B59B6", tasks: [], number: 3 }, // Small house
      { name: "Pre-Construction", x: 550, y: 120, radius: 40, color: "#2ECC71", tasks: [], number: 4 }, // Big building with truck
      { name: "Construction", x: 600, y: 280, radius: 40, color: "#FF6B6B", tasks: [], number: 5 }, // Construction site with ladder
      { name: "Post Construction", x: 700, y: 320, radius: 40, color: "#4A90E2", tasks: [], number: 6 } // Happy family house
    ];
  }

  resizeCanvas() {
    const container = this.canvas.parentElement;
    const rect = container.getBoundingClientRect();
    this.canvas.width = Math.min(800, rect.width - 20);
    this.canvas.height = 400;
  }

  start() {
    if (this.isRunning) return;
    this.isRunning = true;
    this.canvas.addEventListener('click', this.handleClick);
    document.addEventListener('keydown', this.handleKeyPress);
    this.generateSpirits();
    this.updateTaskLocations();
    this.gameLoop();
  }

  stop() {
    this.isRunning = false;
    if (this.animationId) {
      cancelAnimationFrame(this.animationId);
      this.animationId = null;
    }
    this.canvas.removeEventListener('click', this.handleClick);
    document.removeEventListener('keydown', this.handleKeyPress);
    this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);
  }

  // Get difficulty distribution from current tasks
  getDifficultyDistribution() {
    const difficultyCount = { common: 0, rare: 0, epic: 0 };

    this.hotPotatoes.forEach(task => {
      if (difficultyCount.hasOwnProperty(task.difficulty)) {
        difficultyCount[task.difficulty]++;
      }
    });

    return difficultyCount;
  }

  // Get task count for specific category and difficulty combination
  getTaskCountByCategoyAndDifficulty(category, difficulty) {
    return this.hotPotatoes.filter(task => {
      const taskCategory = this.mapCategoryToLocation(task.category);
      return taskCategory === category && task.difficulty === difficulty;
    }).length;
  }

  generateSpirits() {
    this.spirits = [];
    const difficultyCount = this.getDifficultyDistribution();
    const totalTasks = this.hotPotatoes.length;

    // Calculate how many spirits of each type to create
    const maxSpirits = Math.min(12, Math.max(4, Math.floor(totalTasks / 3)));
    const totalDifficultyTasks = difficultyCount.common + difficultyCount.rare + difficultyCount.epic;

    let spiritTypes = [];

    if (totalDifficultyTasks > 0) {
      // Create spirits proportional to task difficulties
      const commonSpirits = Math.floor((difficultyCount.common / totalDifficultyTasks) * maxSpirits);
      const rareSpirits = Math.floor((difficultyCount.rare / totalDifficultyTasks) * maxSpirits);
      const epicSpirits = Math.floor((difficultyCount.epic / totalDifficultyTasks) * maxSpirits);

      // Fill the array with difficulty types
      for (let i = 0; i < commonSpirits; i++) spiritTypes.push('common');
      for (let i = 0; i < rareSpirits; i++) spiritTypes.push('rare');
      for (let i = 0; i < epicSpirits; i++) spiritTypes.push('epic');

      // Fill remaining slots randomly if needed
      const remaining = maxSpirits - spiritTypes.length;
      const difficulties = ['common', 'rare', 'epic'];
      for (let i = 0; i < remaining; i++) {
        spiritTypes.push(difficulties[Math.floor(Math.random() * difficulties.length)]);
      }
    } else {
      // Default distribution if no tasks
      for (let i = 0; i < maxSpirits; i++) {
        spiritTypes.push(['common', 'rare', 'epic'][Math.floor(Math.random() * 3)]);
      }
    }

    // Create spirits based on the distribution
    spiritTypes.forEach((difficulty, i) => {
      this.spirits.push({
        x: Math.random() * (this.canvas.width - 60) + 30,
        y: Math.random() * (this.canvas.height - 60) + 30,
        vx: (Math.random() - 0.5) * 2,
        vy: (Math.random() - 0.5) * 2,
        size: this.getSpiritSizeByDifficulty(difficulty),
        difficulty: difficulty,
        color: this.getSpiritColorByDifficulty(difficulty),
        glow: Math.random() * 15 + 10,
        pulsePhase: Math.random() * Math.PI * 2,
        id: i,
        energy: Math.random() * 100 + 50
      });
    });
  }

  getSpiritSizeByDifficulty(difficulty) {
    switch(difficulty) {
      case 'common': return 12 + Math.random() * 6;  // 12-18px
      case 'rare': return 16 + Math.random() * 8;    // 16-24px
      case 'epic': return 20 + Math.random() * 10;   // 20-30px
      default: return 15 + Math.random() * 10;
    }
  }

  getSpiritColorByDifficulty(difficulty) {
    switch(difficulty) {
      case 'common': return '#66B2FF';  // Blue
      case 'rare': return '#9B59B6';    // Purple
      case 'epic': return '#FF6B6B';    // Red/Orange
      default: return `hsl(${Math.random() * 360}, 70%, 60%)`;
    }
  }

  getSpiritImageByDifficulty(difficulty) {
    switch(difficulty) {
      case 'common': return this.images.spirit1;  // spirit1.png
      case 'rare': return this.images.spirit2;    // spirit2.png
      case 'epic': return this.images.spirit3;    // spirit3.png
      default: return null;
    }
  }

  updateTaskLocations() {
    // Clear existing task assignments
    this.locations.forEach(loc => loc.tasks = []);

    // Distribute tasks to locations based on category
    this.hotPotatoes.forEach(task => {
      let targetLocation;

      switch(task.category) {
        case 'Sales':
          targetLocation = this.locations.find(l => l.name === "Sales Office");
          break;
        case 'New Lead':
          targetLocation = this.locations.find(l => l.name === "New Lead");
          break;
        case 'New Customer':
          targetLocation = this.locations.find(l => l.name === "New Customer");
          break;
        case 'Construction':
          targetLocation = this.locations.find(l => l.name === "Construction");
          break;
        case 'Post Construction':
          targetLocation = this.locations.find(l => l.name === "Post Construction");
          break;
        case 'Pre-Construction':
          targetLocation = this.locations.find(l => l.name === "Pre-Construction");
          break;
        default:
          targetLocation = this.locations.find(l => l.name === "Sales Office");
      }

      if (targetLocation) {
        targetLocation.tasks.push(task);
      }
    });
  }

  handleClick(event) {
    const rect = this.canvas.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;

    for (let spirit of this.spirits) {
      const distance = Math.sqrt((x - spirit.x) ** 2 + (y - spirit.y) ** 2);
      if (distance < spirit.size) {
        this.handleSpiritClick(spirit);
        return;
      }
    }

    for (let location of this.locations) {
      const distance = Math.sqrt((x - location.x) ** 2 + (y - location.y) ** 2);
      if (distance < location.radius && location.tasks.length > 0) {
        this.handleLocationClick(location);
        return;
      }
    }
  }

  handleSpiritClick(spirit) {
    let nearestLocation = null;
    let minDistance = Infinity;

    for (let location of this.locations) {
      if (location.tasks.length > 0) {
        const distance = Math.sqrt((spirit.x - location.x) ** 2 + (spirit.y - location.y) ** 2);
        if (distance < minDistance) {
          minDistance = distance;
          nearestLocation = location;
        }
      }
    }

    if (nearestLocation) {
      this.handleLocationClick(nearestLocation);
    }

    // Score based on spirit difficulty
    const scoreMap = { common: 10, rare: 20, epic: 30 };
    const score = scoreMap[spirit.difficulty] || 10;

    this.updateScore(score);
    this.createParticles(spirit.x, spirit.y, `+${score}`, spirit.color);
  }

  handleLocationClick(location) {
    if (location.tasks.length === 0) return;

    const task = location.tasks[0];
    this.selectedTask = task;
    this.showTaskModal = true;

    this.updateScore(25);
    this.createParticles(location.x, location.y, "+25", "#66B2FF");
  }

  handleKeyPress(event) {
    if (event.key === 'Escape') {
      this.showTaskModal = false;
      this.selectedTask = null;
    }
  }

  createParticles(x, y, text, color) {
    for (let i = 0; i < 5; i++) {
      this.particles.push({
        x: x + (Math.random() - 0.5) * 20,
        y: y + (Math.random() - 0.5) * 20,
        vx: (Math.random() - 0.5) * 4,
        vy: -Math.random() * 3 - 1,
        life: 60,
        maxLife: 60,
        color: color,
        text: i === 0 ? text : null,
        size: 4 + Math.random() * 4
      });
    }
  }

  updateSpirits() {
    // Spirits are now static, but we still update their pulse animation
    this.spirits.forEach(spirit => {
      if (!spirit.isStatic) {
        // Legacy code for moving spirits (if any)
        spirit.x += spirit.vx;
        spirit.y += spirit.vy;

        if (spirit.x <= spirit.size || spirit.x >= this.canvas.width - spirit.size) {
          spirit.vx *= -0.8;
          spirit.x = Math.max(spirit.size, Math.min(this.canvas.width - spirit.size, spirit.x));
        }
        if (spirit.y <= spirit.size || spirit.y >= this.canvas.height - spirit.size) {
          spirit.vy *= -0.8;
          spirit.y = Math.max(spirit.size, Math.min(this.canvas.height - spirit.size, spirit.y));
        }

        for (let location of this.locations) {
          if (location.tasks.length > 0) {
            const dx = location.x - spirit.x;
            const dy = location.y - spirit.y;
            const distance = Math.sqrt(dx * dx + dy * dy);

            if (distance > 0 && distance < 150) {
              const force = 0.02 / distance;
              spirit.vx += (dx / distance) * force;
              spirit.vy += (dy / distance) * force;
            }
          }
        }

        spirit.vx *= 0.99;
        spirit.vy *= 0.99;
      }

      // Update pulse animation for all spirits
      spirit.pulsePhase += 0.1;
      spirit.currentGlow = spirit.glow + Math.sin(spirit.pulsePhase) * 5;
    });
  }

  updateParticles() {
    this.particles = this.particles.filter(particle => {
      particle.x += particle.vx;
      particle.y += particle.vy;
      particle.vy += 0.1;
      particle.life--;
      return particle.life > 0;
    });
  }

  render() {
    // Draw background
    if (this.images.background && this.imagesLoaded) {
      // Scale background to fit canvas
      this.ctx.drawImage(this.images.background, 0, 0, this.canvas.width, this.canvas.height);

      // Add slight overlay for better visibility
      this.ctx.fillStyle = 'rgba(0, 20, 40, 0.3)';
      this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);
    } else {
      // Fallback background
      this.ctx.fillStyle = 'rgba(0, 20, 40, 0.1)';
      this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);
    }

    // Draw locations
    this.locations.forEach(location => {
      const taskCount = location.tasks.length;

      // Draw location circle with semi-transparency to show background
      this.ctx.beginPath();
      this.ctx.arc(location.x, location.y, location.radius, 0, Math.PI * 2);
      this.ctx.fillStyle = taskCount > 0 ? location.color + '80' : 'rgba(68, 68, 68, 0.5)'; // Add transparency
      this.ctx.fill();

      this.ctx.strokeStyle = taskCount > 0 ? '#fff' : '#666';
      this.ctx.lineWidth = 3;
      this.ctx.stroke();

      // Location number indicator
      this.ctx.beginPath();
      this.ctx.arc(location.x - 25, location.y - 25, 12, 0, Math.PI * 2);
      this.ctx.fillStyle = '#000';
      this.ctx.fill();
      this.ctx.strokeStyle = '#fff';
      this.ctx.lineWidth = 2;
      this.ctx.stroke();

      this.ctx.fillStyle = '#fff';
      this.ctx.font = 'bold 12px Arial';
      this.ctx.textAlign = 'center';
      this.ctx.fillText(location.number.toString(), location.x - 25, location.y - 20);

      // Task count indicator (red circle)
      if (taskCount > 0) {
        this.ctx.beginPath();
        this.ctx.arc(location.x + 25, location.y - 25, 15, 0, Math.PI * 2);
        this.ctx.fillStyle = '#FF6B6B';
        this.ctx.fill();
        this.ctx.strokeStyle = '#fff';
        this.ctx.lineWidth = 2;
        this.ctx.stroke();

        this.ctx.fillStyle = '#fff';
        this.ctx.font = 'bold 14px Arial';
        this.ctx.textAlign = 'center';
        this.ctx.fillText(taskCount.toString(), location.x + 25, location.y - 20);
      }

      // Location name
      this.ctx.fillStyle = '#fff';
      this.ctx.font = 'bold 11px Arial';
      this.ctx.textAlign = 'center';
      this.ctx.strokeStyle = '#000';
      this.ctx.lineWidth = 3;
      this.ctx.strokeText(location.name, location.x, location.y + 50);
      this.ctx.fillText(location.name, location.x, location.y + 50);
    });

    // Draw spirits
    this.spirits.forEach(spirit => {
      const spiritImage = this.getSpiritImageByDifficulty(spirit.difficulty);

      if (spiritImage && this.imagesLoaded) {
        // Draw glow effect behind image
        const gradient = this.ctx.createRadialGradient(
          spirit.x, spirit.y, 0,
          spirit.x, spirit.y, spirit.size + spirit.currentGlow
        );
        gradient.addColorStop(0, spirit.color + '80');
        gradient.addColorStop(1, 'transparent');

        this.ctx.fillStyle = gradient;
        this.ctx.beginPath();
        this.ctx.arc(spirit.x, spirit.y, spirit.size + spirit.currentGlow, 0, Math.PI * 2);
        this.ctx.fill();

        // Draw sprite image
        const imageSize = spirit.size * 2;
        this.ctx.drawImage(
          spiritImage,
          spirit.x - imageSize/2,
          spirit.y - imageSize/2,
          imageSize,
          imageSize
        );

        // Draw red circle with task count for this spirit's difficulty and its assigned location
        const taskCount = this.getTaskCountByCategoyAndDifficulty(spirit.location.name, spirit.difficulty);

        if (taskCount > 0) {
          // Red circle background
          this.ctx.beginPath();
          this.ctx.arc(spirit.x + spirit.size - 5, spirit.y - spirit.size + 5, 10, 0, Math.PI * 2);
          this.ctx.fillStyle = '#FF6B6B';
          this.ctx.fill();

          // White border
          this.ctx.strokeStyle = '#fff';
          this.ctx.lineWidth = 2;
          this.ctx.stroke();

          // Task count text
          this.ctx.fillStyle = '#fff';
          this.ctx.font = 'bold 10px Arial';
          this.ctx.textAlign = 'center';
          this.ctx.fillText(taskCount.toString(), spirit.x + spirit.size - 5, spirit.y - spirit.size + 9);
        }
      } else {
        // Fallback: draw colored circle with face
        const gradient = this.ctx.createRadialGradient(
          spirit.x, spirit.y, 0,
          spirit.x, spirit.y, spirit.size + spirit.currentGlow
        );
        gradient.addColorStop(0, spirit.color);
        gradient.addColorStop(1, 'transparent');

        this.ctx.fillStyle = gradient;
        this.ctx.beginPath();
        this.ctx.arc(spirit.x, spirit.y, spirit.size + spirit.currentGlow, 0, Math.PI * 2);
        this.ctx.fill();

        this.ctx.fillStyle = spirit.color;
        this.ctx.beginPath();
        this.ctx.arc(spirit.x, spirit.y, spirit.size, 0, Math.PI * 2);
        this.ctx.fill();

        // Draw face
        this.ctx.fillStyle = '#fff';
        this.ctx.beginPath();
        this.ctx.arc(spirit.x - 5, spirit.y - 3, 2, 0, Math.PI * 2);
        this.ctx.arc(spirit.x + 5, spirit.y - 3, 2, 0, Math.PI * 2);
        this.ctx.fill();

        this.ctx.strokeStyle = '#fff';
        this.ctx.lineWidth = 2;
        this.ctx.beginPath();
        this.ctx.arc(spirit.x, spirit.y + 3, 4, 0, Math.PI);
        this.ctx.stroke();

        // Draw red circle with task count for fallback spirits too
        const taskCount = this.getTaskCountByCategoyAndDifficulty(spirit.location.name, spirit.difficulty);

        if (taskCount > 0) {
          // Red circle background
          this.ctx.beginPath();
          this.ctx.arc(spirit.x + spirit.size - 5, spirit.y - spirit.size + 5, 10, 0, Math.PI * 2);
          this.ctx.fillStyle = '#FF6B6B';
          this.ctx.fill();

          // White border
          this.ctx.strokeStyle = '#fff';
          this.ctx.lineWidth = 2;
          this.ctx.stroke();

          // Task count text
          this.ctx.fillStyle = '#fff';
          this.ctx.font = 'bold 10px Arial';
          this.ctx.textAlign = 'center';
          this.ctx.fillText(taskCount.toString(), spirit.x + spirit.size - 5, spirit.y - spirit.size + 9);
        }
      }
    });

    // Draw particles
    this.particles.forEach(particle => {
      const alpha = particle.life / particle.maxLife;
      this.ctx.fillStyle = particle.color + Math.floor(alpha * 255).toString(16).padStart(2, '0');

      if (particle.text) {
        this.ctx.font = 'bold 16px Arial';
        this.ctx.textAlign = 'center';
        this.ctx.fillText(particle.text, particle.x, particle.y);
      } else {
        this.ctx.beginPath();
        this.ctx.arc(particle.x, particle.y, particle.size * alpha, 0, Math.PI * 2);
        this.ctx.fill();
      }
    });

    // Draw task modal
    if (this.showTaskModal && this.selectedTask) {
      this.drawTaskModal();
    }

    // Draw instructions
    this.ctx.fillStyle = 'rgba(0, 0, 0, 0.8)';
    this.ctx.fillRect(10, this.canvas.height - 50, this.canvas.width - 20, 40);

    this.ctx.fillStyle = '#fff';
    this.ctx.font = '14px Arial';
    this.ctx.textAlign = 'left';
    this.ctx.fillText('Click spirits or locations to interact • Spirits show task difficulty • ESC to close modal', 20, this.canvas.height - 25);
  }

  drawTaskModal() {
    const task = this.selectedTask;
    if (!task) return;

    const modalWidth = 350;
    const modalHeight = 250;
    const modalX = (this.canvas.width - modalWidth) / 2;
    const modalY = (this.canvas.height - modalHeight) / 2;

    // Modal background with gradient
    const gradient = this.ctx.createLinearGradient(modalX, modalY, modalX, modalY + modalHeight);
    gradient.addColorStop(0, 'rgba(0, 44, 84, 0.98)');
    gradient.addColorStop(1, 'rgba(0, 20, 40, 0.98)');
    this.ctx.fillStyle = gradient;
    this.ctx.fillRect(modalX, modalY, modalWidth, modalHeight);

    // Modal border with temperature-based color
    const temp = task.temperature || 0;
    let borderColor = '#66B2FF';
    if (temp > 80) borderColor = '#FF6B6B';
    else if (temp > 60) borderColor = '#FFCC00';

    this.ctx.strokeStyle = borderColor;
    this.ctx.lineWidth = 3;
    this.ctx.strokeRect(modalX, modalY, modalWidth, modalHeight);

    // Task title
    this.ctx.fillStyle = '#FFCC00';
    this.ctx.font = 'bold 18px Arial';
    this.ctx.textAlign = 'center';
    this.ctx.fillText(task.title, modalX + modalWidth / 2, modalY + 35);

    // Difficulty and temperature indicators
    const difficultyEmoji = task.difficulty === 'epic' ? '🔥' : task.difficulty === 'rare' ? '⚡' : '💎';
    const tempEmoji = temp > 80 ? '🔥' : temp > 60 ? '🌶️' : temp > 40 ? '♨️' : '🫖';

    this.ctx.fillStyle = '#fff';
    this.ctx.font = '20px Arial';
    this.ctx.fillText(`${difficultyEmoji} ${tempEmoji}`, modalX + modalWidth - 50, modalY + 35);

    // Task details
    this.ctx.fillStyle = '#fff';
    this.ctx.font = '14px Arial';
    this.ctx.textAlign = 'left';

    const details = [
      `Category: ${task.category}`,
      `Difficulty: ${task.difficulty}`,
      `Value: ${task.value?.toLocaleString() || 0}`,
      `Holder: ${this.teamStats[task.holder]?.name || 'Unknown'}`,
      `Temperature: ${Math.round(temp)}°`,
      `Time Left: ${task.timeLeft || 0}s`,
      `Passes: ${task.passCount || 0}`
    ];

    details.forEach((detail, index) => {
      this.ctx.fillText(detail, modalX + 20, modalY + 65 + index * 20);
    });

    // Progress bar for temperature
    const barWidth = modalWidth - 40;
    const barHeight = 8;
    const barX = modalX + 20;
    const barY = modalY + 210;

    // Background bar
    this.ctx.fillStyle = '#333';
    this.ctx.fillRect(barX, barY, barWidth, barHeight);

    // Temperature bar
    this.ctx.fillStyle = temp > 80 ? '#FF6B6B' : temp > 60 ? '#FFCC00' : '#66B2FF';
    this.ctx.fillRect(barX, barY, (barWidth * temp) / 100, barHeight);

    // Action buttons
    const isMyTask = task.holder === this.currentUser;

    if (isMyTask) {
      // Pass button
      this.ctx.fillStyle = '#FFCC00';
      this.ctx.fillRect(modalX + 20, modalY + 220, 100, 25);
      this.ctx.fillStyle = '#000';
      this.ctx.font = 'bold 12px Arial';
      this.ctx.textAlign = 'center';
      this.ctx.fillText('Pass Task', modalX + 70, modalY + 237);

      // Complete button
      this.ctx.fillStyle = '#2ECC71';
      this.ctx.fillRect(modalX + 130, modalY + 220, 100, 25);
      this.ctx.fillStyle = '#fff';
      this.ctx.fillText('Complete', modalX + 180, modalY + 237);

      // Close button
      this.ctx.fillStyle = '#FF6B6B';
      this.ctx.fillRect(modalX + 240, modalY + 220, 80, 25);
      this.ctx.fillStyle = '#fff';
      this.ctx.fillText('Close', modalX + 280, modalY + 237);
    } else {
      // Just close button for tasks not owned by current user
      this.ctx.fillStyle = '#FF6B6B';
      this.ctx.fillRect(modalX + modalWidth/2 - 40, modalY + 220, 80, 25);
      this.ctx.fillStyle = '#fff';
      this.ctx.font = 'bold 12px Arial';
      this.ctx.textAlign = 'center';
      this.ctx.fillText('Close', modalX + modalWidth/2, modalY + 237);
    }

    // Handle button clicks (add event listener if not already added)
    if (!this.modalClickHandler) {
      this.modalClickHandler = (event) => {
        if (!this.showTaskModal) return;

        const rect = this.canvas.getBoundingClientRect();
        const x = event.clientX - rect.left;
        const y = event.clientY - rect.top;

        if (y >= modalY + 220 && y <= modalY + 245) {
          if (isMyTask) {
            if (x >= modalX + 20 && x <= modalX + 120) {
              // Pass button clicked
              this.onTaskClick(task);
              this.showTaskModal = false;
              this.selectedTask = null;
            } else if (x >= modalX + 130 && x <= modalX + 230) {
              // Complete button clicked
              this.onTaskComplete(task);
              this.showTaskModal = false;
              this.selectedTask = null;
              this.updateTaskLocations();
              this.generateSpirits(); // Regenerate spirits when tasks change
            } else if (x >= modalX + 240 && x <= modalX + 320) {
              // Close button clicked
              this.showTaskModal = false;
              this.selectedTask = null;
            }
          } else {
            if (x >= modalX + modalWidth/2 - 40 && x <= modalX + modalWidth/2 + 40) {
              // Close button clicked
              this.showTaskModal = false;
              this.selectedTask = null;
            }
          }
        }
      };
      this.canvas.addEventListener('click', this.modalClickHandler);
    }
  }

  gameLoop() {
    if (!this.isRunning) return;

    this.updateSpirits();
    this.updateParticles();
    this.updateTaskLocations();
    this.render();

    this.animationId = requestAnimationFrame(this.gameLoop);
  }
}

export default HotPotatoGame;
