class HotPotatoGame {
  constructor(canvasId, gameState, updateScore, hotPotatoes, teamStats, currentUser, onTaskClick, onTaskComplete) {
    this.canvas = document.getElementById(canvasId);
    this.ctx = this.canvas.getContext('2d');
    this.hotPotatoes = hotPotatoes;
    this.updateScore = updateScore;
    this.onTaskClick = onTaskClick;
    this.onTaskComplete = onTaskComplete;

    this.isRunning = false;
    this.spirits = [];
    this.locations = this.initializeLocations();
    this.loadImages();
    this.resizeCanvas();
  }

  async loadImages() {
    this.images = {};
    const imageUrls = {
      background: 'myimages/background.png',
      spirit1: 'myimages/spirit1.png',
      spirit2: 'myimages/spirit2.png', 
      spirit3: 'myimages/spirit3.png'
    };

    for (const [key, url] of Object.entries(imageUrls)) {
      const img = new Image();
      img.src = url;
      this.images[key] = img;
    }
  }

  initializeLocations() {
    // Percentage-based coordinates (relative to canvas size)
    // Original positions: 1:(320,780), 2:(310,400), 3:(700,360), 4:(1060,250), 5:(1100,670), 6:(1330,790)
    // Image size: 1536×1024
    return [
      { name: "Sales Office", x: 20.8, y: 76.2, category: "Sales" },           // 320/1536 = 20.8%, 780/1024 = 76.2%
      { name: "New Lead", x: 20.2, y: 39.1, category: "New Lead" },           // 310/1536 = 20.2%, 400/1024 = 39.1%
      { name: "New Customer", x: 45.6, y: 35.2, category: "New Customer" },   // 700/1536 = 45.6%, 360/1024 = 35.2%
      { name: "Pre-Construction", x: 69.0, y: 24.4, category: "Pre-Construction" }, // 1060/1536 = 69.0%, 250/1024 = 24.4%
      { name: "Construction", x: 71.6, y: 65.4, category: "Construction" },   // 1100/1536 = 71.6%, 670/1024 = 65.4%
      { name: "Post Construction", x: 86.6, y: 77.1, category: "Post Construction" } // 1330/1536 = 86.6%, 790/1024 = 77.1%
    ];
  }

  generateSpirits() {
    this.spirits = [];
    const difficulties = ['common', 'rare', 'epic'];

    this.locations.forEach(location => {
      // Convert percentages to actual pixels
      const actualX = (location.x / 100) * this.canvas.width;
      const actualY = (location.y / 100) * this.canvas.height;

      difficulties.forEach((difficulty, index) => {
        // Place spirits side by side horizontally
        const offset = (index - 1) * 25; // -25, 0, +25 pixels apart

        this.spirits.push({
          x: actualX + offset,
          y: actualY,
          difficulty: difficulty,
          location: location,
          size: Math.min(this.canvas.width, this.canvas.height) * 0.025
        });
      });
    });
  }

  getTaskCount(location, difficulty) {
    return this.hotPotatoes.filter(task => 
      task.category === location.category && task.difficulty === difficulty
    ).length;
  }

  resizeCanvas() {
    this.canvas.width = 800;
    this.canvas.height = 400;
  }

  start() {
    this.isRunning = true;
    this.generateSpirits();
    this.gameLoop();
  }

  stop() {
    this.isRunning = false;
  }

  render() {
    // Clear canvas
    this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);

    // Draw background
    if (this.images.background) {
      this.ctx.drawImage(this.images.background, 0, 0, this.canvas.width, this.canvas.height);
    }

    // Draw locations
    this.locations.forEach((location, index) => {
      // Convert percentages to actual pixels
      const actualX = (location.x / 100) * this.canvas.width;
      const actualY = (location.y / 100) * this.canvas.height;
      const radius = Math.min(this.canvas.width, this.canvas.height) * 0.05; // 5% of smaller dimension

      const totalTasks = this.hotPotatoes.filter(task => task.category === location.category).length;

      // Location circle
      this.ctx.beginPath();
      this.ctx.arc(actualX, actualY, radius, 0, Math.PI * 2);
      this.ctx.fillStyle = totalTasks > 0 ? 'rgba(255, 204, 0, 0.5)' : 'rgba(100, 100, 100, 0.5)';
      this.ctx.fill();
      this.ctx.strokeStyle = '#fff';
      this.ctx.lineWidth = 2;
      this.ctx.stroke();

      // Location number
      const numberRadius = Math.min(this.canvas.width, this.canvas.height) * 0.015;
      this.ctx.beginPath();
      this.ctx.arc(actualX - radius * 0.6, actualY - radius * 0.6, numberRadius, 0, Math.PI * 2);
      this.ctx.fillStyle = '#000';
      this.ctx.fill();
      this.ctx.fillStyle = '#fff';
      this.ctx.font = `bold ${Math.min(this.canvas.width, this.canvas.height) * 0.015}px Arial`;
      this.ctx.textAlign = 'center';
      this.ctx.fillText((index + 1).toString(), actualX - radius * 0.6, actualY - radius * 0.6 + numberRadius * 0.3);

      // Task count
      if (totalTasks > 0) {
        const countRadius = Math.min(this.canvas.width, this.canvas.height) * 0.02;
        this.ctx.beginPath();
        this.ctx.arc(actualX + radius * 0.6, actualY - radius * 0.6, countRadius, 0, Math.PI * 2);
        this.ctx.fillStyle = '#FF6B6B';
        this.ctx.fill();
        this.ctx.fillStyle = '#fff';
        this.ctx.font = `bold ${Math.min(this.canvas.width, this.canvas.height) * 0.018}px Arial`;
        this.ctx.fillText(totalTasks.toString(), actualX + radius * 0.6, actualY - radius * 0.6 + countRadius * 0.3);
      }

      // Location name
      this.ctx.fillStyle = '#fff';
      this.ctx.font = `bold ${Math.min(this.canvas.width, this.canvas.height) * 0.014}px Arial`;
      this.ctx.strokeStyle = '#000';
      this.ctx.lineWidth = 3;
      this.ctx.strokeText(location.name, actualX, actualY + radius + 20);
      this.ctx.fillText(location.name, actualX, actualY + radius + 20);
    });

    // Draw spirits
    this.spirits.forEach(spirit => {
      const taskCount = this.getTaskCount(spirit.location, spirit.difficulty);
      const spiritImage = this.getSpiritImage(spirit.difficulty);

      if (spiritImage) {
        // Draw spirit image
        this.ctx.drawImage(spiritImage, spirit.x - 20, spirit.y - 20, 40, 40);
      } else {
        // Fallback circle
        this.ctx.beginPath();
        this.ctx.arc(spirit.x, spirit.y, spirit.size, 0, Math.PI * 2);
        this.ctx.fillStyle = this.getSpiritColor(spirit.difficulty);
        this.ctx.fill();
      }

      if (taskCount > 0) {
        const countRadius = Math.min(this.canvas.width, this.canvas.height) * 0.015;
        const imageSize = spirit.size * 2;

        this.ctx.beginPath();
        this.ctx.arc(spirit.x + imageSize * 0.3, spirit.y - imageSize * 0.3, countRadius, 0, Math.PI * 2);
        this.ctx.fillStyle = '#FF6B6B';
        this.ctx.fill();
        this.ctx.strokeStyle = '#fff';
        this.ctx.lineWidth = 2;
        this.ctx.stroke();

        this.ctx.fillStyle = '#fff';
        this.ctx.font = `bold ${Math.min(this.canvas.width, this.canvas.height) * 0.012}px Arial`;
        this.ctx.textAlign = 'center';
        this.ctx.fillText(taskCount.toString(), spirit.x + imageSize * 0.3, spirit.y - imageSize * 0.3 + countRadius * 0.3);
      }
    });
  }

  getSpiritImage(difficulty) {
    const imageMap = { common: 'spirit1', rare: 'spirit2', epic: 'spirit3' };
    return this.images[imageMap[difficulty]];
  }

  getSpiritColor(difficulty) {
    const colorMap = { common: '#66B2FF', rare: '#9B59B6', epic: '#FF6B6B' };
    return colorMap[difficulty] || '#66B2FF';
  }

  gameLoop() {
    if (!this.isRunning) return;
    this.render();
    requestAnimationFrame(() => this.gameLoop());
  }
}

export default HotPotatoGame;
