    this.ctx.fillRect(modalX + modalWidth/2 - 60, modalY + modalHeight - 60, 120, 40);
    this.ctx.fillStyle = '#fff';
    this.ctx.font = 'bold 16px Arial';
    this.ctx.fillText('Cancel', modalX + modalWidth/2, modalY + modalHeight - 35);
  }

  handleModalClick(mouseX, mouseY) {
    const modalWidth = 400;
    const modalHeight = 300;
    const modalX = (this.canvas.width - modalWidth) / 2;
    const modalY = (this.canvas.height - modalHeight) / 2;
    const buttonY = modalY + modalHeight - 60;

    // Check Cook button
    if (mouseX >= modalX + 50 && mouseX <= modalX + 170 && 
        mouseY >= buttonY && mouseY <= buttonY + 40) {
      this.handleCook();
    }

    // Check Pass button  
    if (mouseX >= modalX + 230 && mouseX <= modalX + 350 && 
        mouseY >= buttonY && mouseY <= buttonY + 40) {
      this.showPassModal = true;
      this.showTaskModal = false;
    }
  }

  handlePassModalClick(mouseX, mouseY) {
    const modalWidth = 500;
    const modalHeight = 400;
    const modalX = (this.canvas.width - modalWidth) / 2;
    const modalY = (this.canvas.height - modalHeight) / 2;

    // Check teammate buttons
    const teammates = Object.entries(this.teamStats).filter(([id]) => id !== this.currentUser);
    const buttonWidth = 200;
    const buttonHeight = 50;
    const startY = modalY + 80;

    teammates.forEach(([id, stats], index) => {
      const buttonX = modalX + 50 + (index % 2) * 220;
      const buttonY = startY + Math.floor(index / 2) * 70;

      if (mouseX >= buttonX && mouseX <= buttonX + buttonWidth && 
          mouseY >= buttonY && mouseY <= buttonY + buttonHeight) {
        this.handlePass(id);
      }
    });

    // Check Cancel button
    if (mouseX >= modalX + modalWidth/2 - 60 && mouseX <= modalX + modalWidth/2 + 60 && 
        mouseY >= modalY + modalHeight - 60 && mouseY <= modalY + modalHeight - 20) {
      this.showPassModal = false;
      this.showTaskModal = true;
    }
  }

  handleCook() {
    if (this.selectedTask) {
      // Earn cash based on task value
      const earnedValue = this.selectedTask.value || 100;
      this.updateScore(earnedValue / 10);

      // Complete the task through the callback
      this.onTaskComplete(this.selectedTask);

      // Close modal and regenerate spirits
      this.showTaskModal = false;
      this.selectedTask = null;
      this.generateSpirits();
    }
  }

  handlePass(toUserId) {
    if (this.selectedTask) {
      // Pass the task through the callback
      this.onTaskClick(this.selectedTask);

      // Close modals
      this.showPassModal = false;
      this.showTaskModal = false;
      this.selectedTask = null;
    }
  }

  getSpiritImage(difficulty) {
    const imageMap = { common: 'spirit1', rare: 'spirit2', epic: 'spirit3' };
    return this.images[imageMap[difficulty]];
  }

  getSpiritColor(difficulty) {
    const colorMap = { common: '#66B2FF', rare: '#9B59B6', epic: '#FF6B6B' };
    return colorMap[difficulty] || '#66B2FF';
  }

  gameLoop() {
    if (!this.isRunning) return;
    this.render();
    requestAnimationFrame(() => this.gameLoop());
  }
}

export default HotPotatoGame;
